import { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";
import axios from "axios";
import HistorySymptoms from "./HistorySymptoms";

const UserProfile = (props) => {
  const [data, setData] = useState();
  const [history,set] = useRef();
    const userId = localStorage.getItem("userId");

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const decodedToken = jwt_decode(token);
      axios.get(`/user/${userId}`).then((res) => {
        setData(res.data[0]);
      });
      axios.get(`/user/${userId}/history`).then((res) => {
        setHistory(res.data[0]);
        console.log("res", res, "data", data);
      });
    } else {
      navigate("/about");
    }
  }, []);

  const isHistory = (history) => {
    if (!history || !history.historyofsymptoms) {
      return <h2>No history yet...</h2>;
    } else {
      return <h2>{history.historyofsymptoms}</h2>;
    }
  };

  return (
    <>
      <h2>User Profile</h2>
      {data ? (
        <>
          <h3>{data.firstname}</h3>
          <h3>{data.lastname}</h3>
          <h3>{data.email}</h3>
          {isHistory(history.current.historyofsymptoms)}
        </>
      ) : (
        <h1>Try AGAIN</h1>
      )}
    </>
  );
};

export default UserProfile;
